    activityItems = @[[NSString stringWithFormat:@"%@.. Descarga esta app: https://itunes.apple.com/us/app/jesus-la-gran-esperanza/id862249030?mt=8",[leccionTextV.text substringToIndex:190]]];
    
    
    UIActivityViewController *activityController =
    [[UIActivityViewController alloc]
     initWithActivityItems:activityItems
